<script setup>
import Config from "../composables/Config";
import ConfigStore from "../store/ConfigStore";

const plan = usePlanStore();
const user = useUserStore();
const Sites = useSitesStore();
const Wallets = useWalletsStore();
const isPopupVisible = ref(false);
const cn = new Config();
const department_section = "layouts/default/navbar/right/department";
const workspaces = ref(null);
const actived_navbar = ref("department");
const active_section = ref("");
const active_site = ref("");
const selected_tools_section = ref("content-creation");
const runtimeConfig = useRuntimeConfig();
const DEV_ENV = runtimeConfig.public.DEV_ENV;
const active_accordion = ref("");
const route = useRoute();

const reload_store = () => {
  ConfigStore.reload()
    .then(() => {
      auth.value = {
        name: user.name,
        wallet: Wallets.list[0].balance ?? 0,
        subscription:
          cn.by_route(`constants/plans/${plan.plan.name}`) +
          " " +
          cn.by_route(`constants/packages/${plan.plan.package}`),
      };
    })
    .catch((error) => {
      console.error("Error reloading store:", error);
    });
};

const auth = ref({
  name: "",
  wallet: 0,
  subscription: "",
});

onBeforeMount(() => {
  active_section.value = route.path.split("/")[1].toLowerCase();
  active_accordion.value =
    cn.by_route(`pages/${route.path.split("/")[1].toLowerCase()}/accordion`) ?? "";
  reload_store();
});

function change_selected_tools_section(title) {
  selected_tools_section.value = title === selected_tools_section.value ? "" : title;
}

function change_active_section(section) {
  active_section.value = section;
}
function profile() {
  return auth.value;
}
function show_hide_navbar() {
  let right_navbar = document.getElementById("right_navbar");
  if (right_navbar.style.display === "none") {
    right_navbar.style.display = "flex";
  } else {
    right_navbar.style.display = "none";
  }
}
function change_active_accordion(accordion) {
  if (active_accordion.value === accordion) {
    active_accordion.value = "";
  } else {
    active_accordion.value = accordion;
  }
}
</script>
